static int
xxxmixer_init(struct snd_mixer *m)
{
    struct xxx_info   *sc = mix_getdevinfo(m);
    u_int32_t v;

    [初始化硬件]

    [为播放混音器设置v中适当的位]
    mix_setdevs(m, v);
    [为录音混音器设置v中适当的位]
    mix_setrecdevs(m, v)

    return 0;
}

static int
xxxmixer_set(struct snd_mixer *m, unsigned dev,
                 unsigned left, unsigned right)
{
    struct sc_info *sc = mix_getdevinfo(m);
    [设置音量级别(level)]
    return left | (right << 8);
}

